const [text, setText] = useState('Enter text here');

export default function TextForm(props) {
    return (
        <div>
            <h3>{props.heading}</h3>
            <div className="mb-3">
            <textarea className="form-control" id="myBox" rows="6"></textarea>
            </div>
            <button className="btn btn-primary">Convert to Uppercase</button>
            <button className="btn btn-primary">Convert to Uppercase</button>
        </div>
    )
}
